/**
 * Helper para criar botão com ícone
 *
 * @param string $icon Nome do ícone
 * @param string $href URL do link
 * @param string $color Cor do botão (primary, warning, danger, etc)
 * @param string $tooltip Texto do tooltip
 * @param int $size Tamanho do ícone
 * @return string HTML do botão
 */
if (!function_exists('iconButton')) {
function iconButton($icon, $href, $color = 'primary', $tooltip = '', $size = 18) {
    $tooltipAttr = $tooltip ? 'data-tooltip="' . htmlspecialchars($tooltip) . '"' : '';
    return '<a href="' . htmlspecialchars($href) . '" class="btn-icon btn-' . $color . '" ' . $tooltipAttr . '>' . icon($icon, '', $size) . '</a>';
}
}
